namespace Ninjadog.Core.Helpers;

public static class SourceGenerationHelper
{
    public static string Header => $"""
        //------------------------------------------------------------------------------
        // <auto-generated>
        //     This code was generated by the Ninjadog source generator.
        //
        //     - developed by: {Generator.CompanyName} - {Generator.DeveloperName}
        //     - generated on: {Generator.GenerationDate}
        //     - version     : {Generator.Version}
        //
        //     Changes to this file may cause incorrect behavior and will be lost if
        //     the code is regenerated.
        // </auto-generated>
        //------------------------------------------------------------------------------
        """;

    public const string Attribute = """

        namespace Ninjadog;

        [System.AttributeUsage(System.AttributeTargets.Class)]
        public class NinjadogModelAttribute : System.Attribute
        {
        }
        """;

    public static string NullableEnable => """

        #nullable enable
        """;

    public static string NullableDisable => """

        #nullable disable
        """;
}

public class Generator
{
    public const string DeveloperName = "Philippe Matray";
    public const string CompanyName = "Atypical Consulting SRL";

    public static string GenerationDate
        => DateTime.Now.ToString("R");

    public static string Version
        => typeof(Generator).Assembly.GetName().Version.ToString();
}
